Vue.component("hello-world",{data:function(){return{test:"hi"}},template:`
    <div>
        {{test}}
    </div>
    `});
Vue.component("wellcome-modal",{data:function(){return{disable_btn:!1,name:"",name_err:!1,company:""}},template:`
    <div>
        <form @submit.prevent="submitWellcome">
            <input type="text" v-model="name" :class="[name_err ? 'error' : '']" />
            <input type="text" v-model="company" />
            <button type="submit" :disabled="disable_btn">Send</button>
        </form>
    </div>
    `,methods:{submitWellcome:function(){this.disable_btn=!0,0<this.name.length?this.$emit("submit_wellcome_modal",{name:this.name,company:this.company}):(this.name_err=!0,this.disable_btn=!1)}}});